mapper.update:
UPDATE cafehi_qna
SET qna_title=#{qna_title}, qna_title_classification=#{qna_title_classification}, qna_content=#{qna_content}, qna_updatetime=#{qna_updatetime}, upload_path=#{upload_path}, store_file_name=#{store_file_name}, upload_file_name=#{upload_file_name}
WHERE qna_num=#{qna_num}

mapper.select:
SELECT qna_num, qna_title,qna_title_classification, qna_content, qna_writetime, qna_hit, member_id FROM
( SELECT qna_num, qna_title,qna_title_classification, qna_content, qna_writetime, qna_hit, cm.member_code, cm.member_id, row_number() OVER(ORDER BY qna_num DESC) as rNum FROM cafehi_qna cq, cafehi_member cm WHERE cq.member_code = cm.member_code AND cm.member_code = #{member_code}
AND qna_title like '%' || #{scri.keyword} || '%' AND qna_content like '%' || #{scri.keyword} || '%'
) cqm
WHERE rNum BETWEEN #{scri.rowStart} AND #{scri.rowEnd} ORDER BY qna_num DESC


SELECT qna_num, qna_title, qna_title_classification, qna_content, qna_writetime, qna_updatetime, qna_hit, member_id, upload_path, store_file_name, upload_file_name 

FROM cafehi_qna cq JOIN cafehi_member cm

ON  cq.member_code = cm.member_code and cq.qna_num=#{qna_num}
